    10â†’/**
    11â†’ * @title OidcSignatureVerification
    12â†’ * @dev Contract for managing and verifying RSA public keys associated with OIDC (OpenID Connect) JWT signatures.
    13â†’ * Allows adding, removing, and verifying RSA public keys by `kid` (Key ID).
    14â†’ */
    15â†’contract OidcSignatureVerification is Ownable {
    16â†’    /// @notice Event emitted when a new OIDC RSA public key is added
    17â†’    /// @param kid Key ID associated with the RSA public key
    18â†’    /// @param e The exponent of the RSA public key
    19â†’    /// @param n The modulus of the RSA public key
    20â†’    event PubKeyAdded(bytes indexed kid, bytes e, bytes n);
    21â†’
    22â†’    /// @notice Event emitted when an OIDC RSA public key is removed
    23â†’    /// @param kid Key ID associated with the RSA public key that was removed
    24â†’    event PubKeyRemoved(bytes indexed kid);
    25â†’
    26â†’    /// @notice Mapping of RSA public keys by Key ID (`kid`)
    27â†’    /// Each `kid` maps to an RSAPubKey struct containing the RSA public key parameters
    28â†’    mapping(bytes kid => RSAPubKey) internal pubKeys;
    29â†’
    30â†’    /**
    31â†’     * @dev Constructor that initializes the contract with the deployer as the initial owner.
    32â†’     */
    33â†’    constructor() Ownable(msg.sender) {}
    34â†’
    35â†’    /**
    36â†’     * @dev Returns the token type handled by this verifier, which is `"OIDC"`.
    37â†’     * @return The token type identifier as bytes ("OIDC").
    38â†’     */
    39â†’    function tokenType() external pure returns (bytes memory) {
    40â†’        return bytes("OIDC");
    41â†’    }
    42â†’
    43â†’    /**
    44â†’     * @dev Adds an OIDC RSA public key, identified by its Key ID (`kid`), to the contract.
    45â†’     * This key will be used to verify JWT signatures associated with the `kid`.
    46â†’     * Only the contract owner can call this function.
    47â†’     * @param kid The Key ID (identifier) for the RSA public key.
    48â†’     * @param e The exponent component of the RSA public key.
    49â†’     * @param n The modulus component of the RSA public key.
    50â†’     */
    51â†’    function addPubKey(bytes memory kid, bytes memory e, bytes memory n) external onlyOwner {
    52â†’        pubKeys[kid] = RSAPubKey({e: e, n: n});
    53â†’        emit PubKeyAdded(kid, e, n);
    54â†’    }
    55â†’
    56â†’    /**
    57â†’     * @dev Removes an OIDC RSA public key from the contract by its Key ID (`kid`).
    58â†’     * This action can only be performed by the contract owner.
    59â†’     * @param kid The Key ID (identifier) for the RSA public key to be removed.
    60â†’     */
    61â†’    function removePubKey(bytes memory kid) external onlyOwner {
    62â†’        // Check if the key exists in the mapping
    63â†’        if (pubKeys[kid].n.length == 0) {
    64â†’            revert("Public key does not exist");
    65â†’        }
    66â†’
    67â†’        // Remove the RSA public key
    68â†’        delete pubKeys[kid];
    69â†’
    70â†’        // Emit an event indicating the public key has been removed
    71â†’        emit PubKeyRemoved(kid);
    72â†’    }
    73â†’
    74â†’    /**
    75â†’     * @dev Verifies the RSA signature of a JWT.
    76â†’     * Constructs the signing input from the JWT header and payload, computes its SHA256 hash, and verifies
